CREATE TABLE `lhc_telegram_chat` (  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT, `chat_id` bigint(20) unsigned NOT NULL, `tchat_id` bigint(20) unsigned NOT NULL, `bot_id` int(11) NOT NULL, `ctime` int(11) NOT NULL, `utime` int(11) NOT NULL, PRIMARY KEY (`id`),  KEY `tchat_id_utime` (`tchat_id`,`utime`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE `lhc_telegram_bot` (  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT, `bot_username` varchar(20) NOT NULL, `bot_api` varchar(50) NOT NULL, `dep_id` int(11) unsigned NOT NULL, `webhook_set` int(11) unsigned NOT NULL, `chat_timeout` int(11) unsigned NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE `lhc_telegram_signature` (  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT, `bot_id` int(11) unsigned NOT NULL, `user_id` int(11) unsigned NOT NULL, `signature` text NOT NULL, PRIMARY KEY (`id`), KEY `bot_id_user_id` (`bot_id`,`user_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
